con:configuration.scriptText:
 import groovy.json.JsonSlurper

def response = context.expand( '${CreateCustomer#Response}')

def sluper = new JsonSlurper()
def json = sluper.parseText(response)

def email = json.email

if(email=='test@test.com')
{
	assert true
}
else
{
	assert  false : "Mismatch of emailaddress"
}

import groovy.json.JsonSlurper

def response = context.expand( '${GetCustomer#Response}')

def sluper = new JsonSlurper()
def json = sluper.parseText(response)

def fname = json.firstName
def lname = json.lastName
def emailaddress = json.email
def dob = json.dateOfBirth
def phone = json.phoneNumber

if((fname=='prince') && (lname=='harjai') && (emailaddress=='test@test.com') && (dob=='2018-02-14T15:18:46.87Z') && (phone==242423))
	{
		assert true
	}
else
{
	assert false
}

